import { createSlice } from '@reduxjs/toolkit';

export const foodSlice = createSlice({
  name: 'foods',
  initialState: [],
  reducers:{
      update: (state, action) =>{
        state.push(action.payload)
      },
      remove: (state, action) => (
        {...state, state:state.items.filter(item => action.payload !== item) }
      )
  }
})

export const {update, remove} = foodSlice.actions;
export default foodSlice.reducer;
