import { createSlice } from '@reduxjs/toolkit';

export const foodSlice = createSlice({
  name: 'foods',
  initialState: {item:[]},
  reducers:{
      update: (state, action) => (
        {...state, item: state.item.push(action.payload)}
      ),
      remove: (state, action) => (
        {...state }
      )
  }
})

export const {update, remove} = foodSlice.actions;
export default foodSlice.reducer;
