import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux';
import { update, remove } from '../../redux/foodSlice';
import c from "./images/c.jpg"

const Foodlist = (props) => {
    const [foods, setFoods] = useState()
    const foodItem = useSelector((state) => state.foods);
    const dispatch = useDispatch();

    useState(async () => {
        try {
            const res = await fetch('/get', {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                },
            })
            const Data = await res.json();
            for (let item of foodItem) {
                hideButton(item.id)
            }
            setFoods(Data)

        } catch (error) {
            console.log(error);
        }

    }, [])

    const hideButton = (id) => {
        const button = document.getElementById(id)
        button.innerText = 'Item Added'
        button.className = 'btn'
        button.disabled = true
    }

    const addFood = (id, name, category, subcategory, price) => {
        dispatch(update({ id, name, category, subcategory, price }));
        hideButton(id)
    }

    return (
        <div className="container my-5">
            <div className="row">
                <h1 className="text-center" id="foodlist-title">{props.subcategory.toUpperCase()}</h1>
                {foods && foods.map((element, i) => {
                    return <>
                        <div className="col-md-4 my-3">
                            <div className="card bg-light mb-3" style={{ width: "18rem" }}>
                                <img src={c} className="card-img-top" alt="..." />
                                <div className="card-body">
                                    <p className="card-text">{element.desc}</p>
                                    <h5 className="card-title text-center">{element.name}</h5>
                                    <h5 className="card-title text-center text-danger">₹ {element.price}/-</h5>
                                    <div className="text-center">
                                        <button className="btn btn-dark" id={element._id} onClick={() => addFood(element._id, element.name, element.category, element.subcategory, element.price)}>Add in Basket</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </>
                })}

            </div>
        </div>
    )
}


export default Foodlist
