import { axios } from "./network";

const getDishes = ( ) =>
new Promise((resolve, reject) => {
  axios({
    method: 'GET',
    url: `/all-Active-Dishes`
  })
    .then(({ data }) => {
      resolve(data);
    })
    .catch(error => {
      reject(error.response ? error.response.data : {});
    });
});

const getAllDishes = ( ) =>
new Promise((resolve, reject) => {
  axios({
    method: 'GET',
    url: `/allDishes`
  })
    .then(({ data }) => {
      resolve(data);
    })
    .catch(error => {
      reject(error.response ? error.response.data : {});
    });
});

const addDish = ( data ) =>
new Promise((resolve, reject) => {
  axios({
    method: 'POST',
    url: `/addDish`,
    data
  })
    .then(({ data }) => {
      resolve(data);
    })
    .catch(error => {
      reject(error.response ? error.response.data : {});
    });
});

export { getDishes, getAllDishes, addDish };
